mapper.insert:
INSERT INTO BOARD (
   	BOARD_NUM
    , TITLE
    , CONTENT
    , WRITER
    , IS_PRIVATE
) VALUES (
    (SELECT NVL(MAX(BOARD_NUM), 0) + 1 FROM BOARD)
    , #{title}
    , #{content}
    , #{writer}
    , 'N'
)

INSERT INTO BOARD_MEMBER(
	MEM_ID
	, MEM_PW
	, MEM_NAME
	, MEM_AGE
	, GENDER
	, IS_ADMIN
) VALUES (
	#{memId}
	, #{memPw}
	, #{memName}
	, #{memAge}
	, #{gender}
	, 'N'
)

mapper.update:
UPDATE BOARD
SET READ_CNT = READ_CNT + 1
WHERE BOARD_NUM = #{boardNum}
